Ext.define('COMMON.simpleIframe', {
    extend:  Ext.Panel ,
    alias: 'widget.simpleIframe',
    src: 'about:blank',
    border: false,
    height: '100%',
    loadingText: 'Loading ...',
    initComponent: function () {
        this.updateHTML();
        this.callParent(arguments);
    },
    updateHTML: function () {
        this.html = '<iframe id="iframe-' + this.id + '"' +
        ' style="overflow:auto;width:100%;height:100%"' +
        ' marginheight="0" hspace="0" scrolling="yes" frameBorder="0" ' +
        ' src="' + this.src + '"' +
        '></iframe>';
    },
    reload: function () {
        this.setSrc(this.src);
    },
    reset: function () {
        var iframe = this.getDOM();
        var iframeParent = iframe.parentNode;
        if (iframe && iframeParent) {
            iframe.src = 'about:blank';
            iframe.parentNode.removeChild(iframe);
        }

        iframe = document.createElement('iframe');
        iframe.frameBorder = 0;
        iframe.src = this.src;
        iframe.id = 'iframe-' + this.id;
        iframe.style.overflow = 'auto';
        iframe.style.width = '100%';
        iframe.style.height = '100%';
        iframe.style.zIndex = '-1';
        iframeParent.appendChild(iframe);
    },
    setSrc: function (src, loadingText) {
        this.src = src;
        var iframe = this.getDOM();
        if (iframe) {
            iframe.src = src;
        }
    },
    getSrc: function () {
        return this.src;
    },
    getDOM: function () {
        return document.getElementById('iframe-' + this.id);
    },
    getDocument: function () {
        var iframe = this.getDOM();
        iframe = (iframe.contentWindow) ? iframe.contentWindow : (iframe.contentDocument.document) ? iframe.contentDocument.document : iframe.contentDocument;
        return iframe.document;
    },
    destroy: function () {
        var iframe = this.getDOM();
        if (iframe && iframe.parentNode) {
            iframe.src = 'about:blank';
            iframe.parentNode.removeChild(iframe);
        }
        this.callParent(arguments);
    },
    update: function (content) {
        this.setSrc('about:blank');
        try {
            var doc = this.getDocument();
            doc.open();
            doc.write(content);
            doc.close();
        } catch (err) {
            // reset if any permission issues
            this.reset();
            var doc = this.getDocument();
            doc.open();
            doc.write(content);
            doc.close();
        }
    }
});

